import React from "react";
import "./Cards.css";
import error_Image from '../../res/scape_back_logo.png'

class Cards extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      show: false
    };
  }

  displayCards() {
    if (this.state.show) {
      if (this.props.cards) {
        return (
          <div className={"Card_Slider"}>
            {Object.values(this.props.cards).map((card) => {
              return (
                <React.Fragment >
                  <div className = {"Card_Data"}>
                    <img
                      src={card.image}
                      onError={(e) => {
                        e.target.src = error_Image;
                      }}
                    />
                    <h1>{card.name} </h1>
                    <h3>{card.text}</h3>
                  </div>
                </React.Fragment>
              );
            })}
          </div>
        );
      } else {
        return <h1>no cards added</h1>;
      }
    } else {
      return null;
    }
  }

  render() {
    return (
      <div className={"Card"}>
        <div className={"Title"}>Cards</div>
        <button className = {"CardBtn"}
          onClick={() => {
            this.setState({ show: !this.state.show });
          }}
        >
          Cards
        </button>
        {this.state.show ? <h1>Open</h1> : <h1>Close</h1>}
        {this.displayCards()}
      </div>
    );
  }
}

export default Cards;
